import React, { useState, useEffect } from "react";
import "./style.css";

interface IData {
  code: string;
  title: string;
}

interface AutoCompleteProps {
  data: IData[];
}

const AutoComplete: React.FC<AutoCompleteProps> = ({ data }) => {
  const [inputValue, setInputValue] = useState<string>("");
  const [options, setOptions] = useState<IData[]>([]);
  const [debouncedInputValue, setDebouncedInputValue] = useState<string>("");
  let timeoutId: NodeJS.Timeout | null = null;
  console.log(data);

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setDebouncedInputValue(inputValue);
    }, 300);

    return () => clearTimeout(timeoutId);
  }, [inputValue]);

  useEffect(() => {
    filterResults();
  }, [debouncedInputValue]);

  const onInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value.replace(/[^a-zA-Z0-9\s]/g, "");
    setInputValue(value);

  };

  const filterResults = () => {
    let filteredOptions: IData[] = [];
    if (debouncedInputValue.trim().length > 0) {
      const regex = new RegExp(`^${debouncedInputValue}`, "i");
      filteredOptions = data.filter((item: IData) => regex.test(item.title));
    }
    setOptions(filteredOptions);
  };

  const escapeHTML = (text: string) => {
    return text.replace(/[&<>"']/g, (match) => {
      switch (match) {
        case "&":
          return "&amp;";
        case "<":
          return "&lt;";
        case ">":
          return "&gt;";
        case '"':
          return "&quot;";
        case "'":
          return "&#39;";
        default:
          return match;
      }
    });
  };

  return (
    <>
      <div className="container">
        <h1>Autocomplete</h1>
        <p>Find here.</p>
        <input
          type="text"
          onChange={onInputChange}
          placeholder="I am looking for.."
          value={inputValue}
        ></input>
      </div>
      <div>
        {options.length > 0 ? (
          <ul>
            {options.map((item) => {
              return (
                <li key={item.code}>
                  <span dangerouslySetInnerHTML={{ __html: escapeHTML(item.title) }} />
                </li>
              );
            })}
          </ul>
        ) : (
          <ul>
            {inputValue.trim().length > 0 && <li><span>no result found.</span></li>}
          </ul>
        )}
      </div>
    </>
  );
};

export default AutoComplete;
