import React, { useState, useEffect } from "react";
import "./style.css";

interface IData {
  code: string;
  title: string;
}

interface AutoCompleteProps {
  data: IData[];
}

const AutoComplete: React.FC<AutoCompleteProps> = ({ data }) => {
  const [inputValue, setInputValue] = useState<string>("");
  const [options, setOptions] = useState<IData[]>([]);
  const [debouncedInputValue, setDebouncedInputValue] = useState<string>("");
  let timeoutId: NodeJS.Timeout | null = null;
  console.log(data);

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setDebouncedInputValue(inputValue);
    }, 1000);

    return () => {
      return clearTimeout(timeoutId); //clears previous timeout fun, component unmount
    };
  }, [inputValue]);

  useEffect(() => {
    filterResults();
  }, [debouncedInputValue]);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value.replace(/[^a-zA-Z0-9\s]/g, "");
    setInputValue(value);
  };

  const filterResults = () => {
    let filteredOptions: IData[] = [];
    if (debouncedInputValue.trim().length > 0) {
      const regex = new RegExp(`^${debouncedInputValue}`, "i");
      filteredOptions = data.filter((item: IData) => regex.test(item.title));
    }
    setOptions(filteredOptions);
  };

  const clearInputValue = () => {
    setInputValue("");
    setOptions([]);
  };

  const highlightMatch = (text: string, inputValue: string) => {
    const regex = new RegExp(`(${inputValue})`, "gi");
    return text.replace(regex, '<span class="highlight">$1</span>');
  };

  return (
    <div className="container">
      <h1>Autocomplete</h1>
      <p>Find here.</p>
      <div>
        <input
          type="text"
          onChange={handleChange}
          placeholder="I am looking for.."
          value={inputValue}
        />
        <button className="buttonClear" onClick={clearInputValue}>
          X
        </button>
      </div>
      <div>
        {options.length > 0 ? (
          <ul>
            {options.map((item) => {
              return (
                <li key={item.code}>
                  <span
                    dangerouslySetInnerHTML={{
                      __html: highlightMatch(item.title, inputValue),
                    }}
                  />
                </li>
              );
            })}
          </ul>
        ) : (
          <ul>
            {inputValue.trim().length > 0 && (
              <li>
                <span>no result found.</span>
              </li>
            )}
          </ul>
        )}
      </div>
    </div>
  );
};

export default AutoComplete;
