import React, { useState } from "react";

interface IData {
  name: string;
  code: string;
}

interface AutoCompleteProps {
  data: IData[];
}

const AutoComplete: React.FC<AutoCompleteProps> = ({ data }) => {
  const [inputValue, setInputValue] = useState<string>("");
  const [options, setOptions] = useState<IData[]>([]);
  console.log(data);

  const onInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value;
    setInputValue(value);
    let options = [];
    if (value.length > 0) {
      const regex = new RegExp(`^${value}`, "i"); //read Regex basics
      options = data.sort().filter((item: IData) => regex.test(item.name)); //read filter, map, reduce
      setOptions(options);
      console.log(options);
    }
  };
  return (
    <>
      <div>
        <input
          type="text"
          onChange={onInputChange}
          placeholder="I am looking for.."
        ></input>
      </div>
      <div>
        <ul>
          {options.map((item) => {
            return (
              <li key={item.code}>
                <span>{item.name}</span>
              </li>
            );
          })}
        </ul>
      </div>
    </>
  );
};

export default AutoComplete;
