import React, { useState } from "react";
import "./style.css";

interface IData {
  code: string;
  title: string;
}

interface AutoCompleteProps {
  data: IData[];
}

const AutoComplete: React.FC<AutoCompleteProps> = ({ data }) => {
  const [inputValue, setInputValue] = useState<string>("");
  const [options, setOptions] = useState<IData[]>([]);
  console.log(data);

  const onInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value;
    setInputValue(value.replace(/[^a-zA-Z0-9\s]/g, ''));

    const escapeHTML = (text: string) => {
        return text.replace(/[&<>"']/g, function (match) {
          return {
            '&': '&amp;',
            '<': '&lt;',
            '>': '&gt;',
            '"': '&quot;',
            "'": '&#39;'
          }[match];
        });
      };

    let options = [];
    if (value.length > 0) {
      const regex = new RegExp(`^${value}`, "i"); //read Regex basics
      options = data.sort().filter((item: IData) => regex.test(item.title)); //read filter, map, reduce
      setOptions(options);
      console.log(options);
    }
  };
  return (
    <>
      <div className="container">
        <h1>Autocomplete</h1>
        <p>Find here.</p>
        <input
          type="text"
          onChange={onInputChange}
          placeholder="I am looking for.."

        ></input>
      </div>
      <div>
        {options.length > 0 ? (
          <ul>
            {options.map((item) => {
              return (
                <li key={item.code} dangerouslySetInnerHTML={{ __html: escapeHTML(options) }}>
                  <span>{item.title}</span>
                </li>
              );
            })}
          </ul>
        ) : (
          <ul>
            {inputValue.length > 0 && (
              <li>
                <p>no result found, please provide another search criteria</p>
              </li>
            )}
          </ul>
        )}
      </div>
    </>
  );
};

export default AutoComplete;
